INSERT INTO `tintuc` (`idtintuc`, `tieude`, `slugtintuc`, `motangan`, `motanganseo`, `tukhoa`, `hinhdaidien`, `ngaythang`, `noidung`, `anhien`, `hot`, `iddmtintuc`) VALUES
(14, 'Giật Mình Khi Ngọc Trinh Đi Xe Máy Giá Trị Chưa Bằng Đôi Giày', 'giat-minh-khi-ngoc-trinh-di-xe-may-gia-tri-chua-bang-doi-giay', '<p style=\"text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">Nổi tiếng l&agrave; một người vừa c&oacute; bạn trai đại gia, vừa c&oacute; khả năng kiếm tiền tỷ, thế nhưng đ&ocirc;i khi Ngọc Trinh cũng di chuyển bằng xe m&aacute;y tay ga trị gi&aacute; hơn 20 triệu đồng đầy bất ngờ.</span></span></p>\n', 'Giật Mình Khi Ngọc Trinh Đi Xe Máy Giá Trị Chưa Bằng Đôi Giày', 'Nổi tiếng là một người vừa có bạn trai đại gia, vừa có khả năng kiếm tiền tỷ, thế nhưng đôi khi Ngọc Trinh cũng di chuyển bằng xe máy tay ga trị giá hơn 20 triệu đồng đầy bất ngờ.', '713776.jpg', '15/07/2015 12:11', '<p style=\"text-align:justify\">Mặc d&ugrave; kh&ocirc;ng hoạt động nghệ thuật, nhưng Ngọc Trinh vẫn l&agrave; c&aacute;i t&ecirc;n được quan t&acirc;m h&agrave;ng đầu trong showbiz Việt. Mỗi lần xuất hiện, c&ocirc; cũng lu&ocirc;n khiến kh&aacute;n giả phải cho&aacute;ng ngợp với xe hơi tiền tỷ, quần &aacute;o v&agrave; gi&agrave;y d&eacute;p hạng hiệu, mỗi m&oacute;n trị gi&aacute; v&agrave;i chục triệu đồng.</p>\n\n<p style=\"text-align:justify\">Thế nhưng, đ&ocirc;i lần Ngọc Trinh lại di chuyển tr&ecirc;n những chiếc xe m&aacute;y, gi&aacute; trị chỉ hơn 20 triệu đồng trong sự t&ograve; m&ograve; của người đi đường. Chiếc xe thậm ch&iacute; c&ograve;n chưa bằng một đ&ocirc;i gi&agrave;y h&agrave;ng hiệu m&agrave; Ngọc Trinh từng khoe.</p>\n\n<p style=\"text-align:justify\">&nbsp;</p>\n\n<div class=\"imgslide\" style=\"border: 0px; font-size: 15px; margin: 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; cursor: -webkit-zoom-in; line-height: 18.5714282989502px; text-align: center; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\"><img src=\"http://v2.cdn.ringring.vn/JgXXlF23iLlZaP3hSsEY/640x10000x2/image/0/0/697/713776.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px; box-sizing:border-box; font-family:arial,sans-serif; line-height:18.5714282989502px; margin:0px; max-width:720px; outline:none 0px; padding:0px; vertical-align:baseline; width:546px\" /></div>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><em>Ngọc Trinh sử dụng xe m&aacute;y để di chuyển thay v&igrave; xế hộp tiền tỷ thường thấy.</em></p>\n\n<p style=\"text-align:center\">&nbsp;</p>\n\n<div class=\"imgslide\" style=\"border: 0px; font-size: 15px; margin: 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; cursor: -webkit-zoom-in; line-height: 18.5714282989502px; text-align: center; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\"><em><img src=\"http://v2.cdn.ringring.vn/JgXXlF23iLlZaP3hSsEY/640x10000x2/image/0/0/697/713782.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px; box-sizing:border-box; margin:0px; max-width:720px; outline:none 0px; padding:0px; vertical-align:baseline; width:546px\" /></em></div>\n\n<p style=\"text-align:center\"><em><img src=\"http://v2.cdn.ringring.vn/JgXXlF23iLlZaP3hSsEY/640x10000x2/image/0/0/697/713779.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px; box-sizing:border-box; margin:0px; max-width:720px; outline:none 0px; padding:0px; vertical-align:baseline; width:546px\" /></em></p>\n\n<p style=\"text-align:center\">&nbsp;</p>\n\n<div class=\"imgslide\" style=\"border: 0px; font-size: 15px; margin: 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; cursor: -webkit-zoom-in; line-height: 18.5714282989502px; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\">&nbsp;</div>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><em>Ngọc Trinh thường xuy&ecirc;n di chuyển bằng xe m&aacute;y tay ga th&ocirc;ng dụng với gi&aacute; trị chưa bằng một đ&ocirc;i gi&agrave;y h&agrave;ng hiệu của c&ocirc;. &nbsp;</em></p>\n\n<p style=\"text-align:center\">&nbsp;</p>\n\n<div class=\"imgslide\" style=\"border: 0px; font-size: 15px; margin: 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; cursor: -webkit-zoom-in; line-height: 18.5714282989502px; text-align: center; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\"><em><img src=\"http://v2.cdn.ringring.vn/JgXXlF23iLlZaP3hSsEY/640x10000x2/image/0/0/697/713920.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px; box-sizing:border-box; margin:0px; max-width:720px; outline:none 0px; padding:0px; vertical-align:baseline; width:546px\" /></em></div>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><em>Đ&ocirc;i gi&agrave;y đế đỏ &aacute;nh kim Ngọc Trinh từng mang c&oacute; gi&aacute; 4,400 USD (khoảng hơn 90 triệu đồng).</em></p>\n\n<p style=\"text-align:justify\">Nhắc đến sự gi&agrave;u c&oacute; của showbiz Việt, kh&ocirc;ng thể thiếu c&aacute;i t&ecirc;n Ngọc Trinh. &quot;Nữ ho&agrave;ng nội y&quot; l&agrave; một trong những mỹ nh&acirc;n đi ti&ecirc;n phong về h&agrave;ng hiệu, người đầu ti&ecirc;n trong showbiz Việt sở hữu Hermes Birkin da c&aacute; sấu trị gi&aacute; gần 1,5 tỷ đồng.&nbsp;</p>\n\n<p style=\"text-align:justify\">&nbsp;</p>\n\n<div class=\"imgslide\" style=\"border: 0px; font-size: 15px; margin: 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; cursor: -webkit-zoom-in; line-height: 18.5714282989502px; text-align: center; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\"><img src=\"http://v2.cdn.ringring.vn/JgXXlF23iLlZaP3hSsEY/640x10000x2/image/0/0/697/713813.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px; box-sizing:border-box; font-family:arial,sans-serif; line-height:18.5714282989502px; margin:0px; max-width:720px; outline:none 0px; padding:0px; vertical-align:baseline; width:546px\" /></div>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><em>Chiếc t&uacute;i c&aacute; sấu trị gi&aacute; gần 1,5 tỷ của Ngọc Trinh.</em>&nbsp;</p>\n\n<p style=\"text-align:justify\">Nhắc về độ chơi trội v&agrave; gi&agrave;u c&oacute; của Ngọc Trinh c&oacute; thể kể đến, &quot;<em>mỗi lần sinh nhật l&agrave; t&ocirc;i đổi xe hơi&quot;</em>&nbsp;m&agrave; Ngọc Trinh từng chia sẻ trong b&agrave;i phỏng vấn. Theo một nguồn tin th&acirc;n cận, chiếc xe hơi đầu ti&ecirc;n Ngọc Trinh sở hữu l&agrave; Kia Morning - một d&ograve;ng xe phổ th&ocirc;ng với gi&aacute; th&agrave;nh tương đối &quot;mềm&quot;, tiếp đ&oacute; l&agrave; xe Huyndai. Rồi c&ocirc; l&ecirc;n đời với d&ograve;ng xe sang Audi A4 m&agrave;u trắng, sau đ&oacute; l&agrave; BMW x&aacute;m 720.</p>\n\n<div style=\"border: 0px; font-size: 15px; margin: 10px 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; line-height: 18.5714282989502px; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\">Chỉ sau khi tậu xe x&aacute;m v&agrave;i th&aacute;ng, Ngọc Trinh g&acirc;y cho&aacute;ng với BMW trắng 750Li trị gi&aacute; 4 tỷ đồng. V&agrave; hiện tại, c&ocirc; n&agrave;ng di chuyển bằng Range Rover m&agrave;u trắng c&oacute; gi&aacute; hơn 8 tỷ đồng.&nbsp;</div>\n\n<p style=\"text-align:justify\">&nbsp;</p>\n\n<div class=\"imgslide\" style=\"border: 0px; font-size: 15px; margin: 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; cursor: -webkit-zoom-in; line-height: 18.5714282989502px; text-align: center; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\"><img src=\"http://v2.cdn.ringring.vn/JgXXlF23iLlZaP3hSsEY/640x10000x2/image/0/0/697/713803.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px; box-sizing:border-box; font-family:arial,sans-serif; line-height:18.5714282989502px; margin:0px; max-width:720px; outline:none 0px; padding:0px; vertical-align:baseline; width:546px\" /></div>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><em>Chiếc xe sang đầu ti&ecirc;n của Ngọc Trinh thuộc nh&atilde;n hiệu Audi d&ograve;ng A4.</em>&nbsp;</p>\n\n<p style=\"text-align:justify\">&nbsp;</p>\n\n<div class=\"imgslide\" style=\"border: 0px; font-size: 15px; margin: 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; cursor: -webkit-zoom-in; line-height: 18.5714282989502px; text-align: center; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\"><img src=\"http://v2.cdn.ringring.vn/JgXXlF23iLlZaP3hSsEY/640x10000x2/image/0/0/697/713807.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px; box-sizing:border-box; font-family:arial,sans-serif; line-height:18.5714282989502px; margin:0px; max-width:720px; outline:none 0px; padding:0px; vertical-align:baseline; width:546px\" /></div>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><em>Ngọc Trinh từng g&acirc;y cho&aacute;ng showbiz Việt khi tậu xe BMW trắng 750Li c&oacute; gi&aacute; hơn 4 tỷ đồng.</em></p>\n\n<p style=\"text-align:center\">&nbsp;</p>\n\n<div class=\"imgslide\" style=\"border: 0px; font-size: 15px; margin: 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; cursor: -webkit-zoom-in; line-height: 18.5714282989502px; text-align: center; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\"><em><img src=\"http://v2.cdn.ringring.vn/JgXXlF23iLlZaP3hSsEY/640x10000x2/image/0/0/697/713804.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px; box-sizing:border-box; margin:0px; max-width:720px; outline:none 0px; padding:0px; vertical-align:baseline; width:546px\" /></em></div>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><em>Hiện tại, nữ người mẫu di chuyển bằng xe hơi gầm cao d&ograve;ng SUV của Range Rover trị gi&aacute; hơn 8 tỷ đồng.</em></p>\n', 0, 0, 1),
(15, '6 Người Chết Vì “Bệnh Lạ” Ở Quảng Nam', '6-nguoi-chet-vi-benh-la-o-quang-nam', '<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">- Suốt hơn 2 th&aacute;ng nay, nhiều người ở Quảng Nam mắc một căn &ldquo;bệnh lạ&rdquo; với triệu chứng đau ở v&ugrave;ng cổ, dẫn đến tử vong.</span></span></p>\n', '6 Người Chết Vì “Bệnh Lạ” Ở Quảng Nam', '- Suốt hơn 2 tháng nay, nhiều người ở Quảng Nam mắc một căn “bệnh lạ” với triệu chứng đau ở vùng cổ, dẫn đến tử vong.', '20150715110720-bl-1.jpg', '15/07/2015 12:21', '<p>S&aacute;ng nay (15/7), Gi&aacute;m đốc Sở Y tế Quảng Nam Nguyễn Văn Hai cho biết, đang chỉ đạo cơ quan chuy&ecirc;n m&ocirc;n tiến h&agrave;nh điều tra dịch tễ học để truy t&igrave;m nguy&ecirc;n nh&acirc;n căn bệnh &ldquo;lạ&rdquo; khiến 6 người tử vong thời gian qua tr&ecirc;n địa b&agrave;n.</p>\n\n<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" class=\"center image\" style=\"background-attachment:scroll; background-clip:initial; background-image:none; background-origin:initial; background-position:0px 0px; background-repeat:repeat; background-size:initial; border-collapse:collapse; border-spacing:0px; border:0px none; font-family:arial; font-size:15px; line-height:23px; margin:0px; outline:none 0px; padding:0px; width:400px\">\n	<tbody>\n		<tr>\n			<td><img alt=\"bệnh lạ; Quảng Nam\" src=\"http://imgs.vietnamnet.vn/Images/vnn/2015/07/15/11/20150715110720-bl-1.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px none; margin:0px auto; max-width:720px; outline:none 0px; padding:0px\" title=\"bệnh lạ; Quảng Nam\" /></td>\n		</tr>\n		<tr>\n			<td>\n			<p>Y tế dự ph&ograve;ng phun h&oacute;a chất nơi l&agrave;ng xuất hiện bệnh lạ</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>Theo b&aacute;o c&aacute;o từ Trung t&acirc;m Y tế huyện Phước Sơn, đ&atilde; c&oacute; 6 người d&acirc;n tử vong do đau ở cổ nhưng chưa x&aacute;c định được đ&oacute; l&agrave; căn bệnh g&igrave;. 6 người tử vong c&oacute; c&ugrave;ng một triệu chứng.</p>\n\n<p>Căn bệnh &ldquo;lạ&rdquo; n&agrave;y xuất hiện hơn 2 th&aacute;ng nay tại một ng&ocirc;i l&agrave;ng nằm s&acirc;u trong rừng thuộc th&ocirc;n 8B, x&atilde; Phước Lộc, huyện Phước Sơn.</p>\n\n<p>X&aacute;c nhận của ng&agrave;nh Y tế huyện Phước Sơn cho biết, 2 người mới chết gần nhất v&agrave;o ng&agrave;y 11 v&agrave; 12/7. Hiện tại, c&oacute; 7 trường hợp ph&aacute;t bệnh đang được điều trị c&aacute;ch ly tại Trung t&acirc;m Y tế huyện.</p>\n\n<p>Ngo&agrave;i ra, c&ograve;n một số trường hợp kh&aacute;c c&oacute; biểu hiện mắc bệnh đang được theo d&otilde;i.</p>\n\n<p>Hiện Sở Y tế Quảng Nam đ&atilde; cử đo&agrave;n c&ocirc;ng t&aacute;c l&ecirc;n địa b&agrave;n xuất hiện bệnh &ldquo;lạ&rdquo; để điều tra v&agrave; b&aacute;o c&aacute;o Viện Pasteur Nha Trang, phối hợp truy t&igrave;m nguy&ecirc;n nh&acirc;n.</p>\n\n<p>Được biết, đ&acirc;y l&agrave; khu vực rừng n&uacute;i, người d&acirc;n thường sử dụng nguồn nước s&ocirc;ng suối. Trong khi đ&oacute;, nguồn nước đầu nguồn đang trong t&igrave;nh trạng &ocirc; nhiễm do nạn khai th&aacute;c v&agrave;ng tr&aacute;i ph&eacute;p với lượng lớn h&oacute;a chất độc hại thải ra.</p>\n\n<p><strong>Vũ Trung</strong></p>\n', 0, 0, 4),
(16, 'Kiều Thanh Được Phong NSƯT', 'kieu-thanh-duoc-phong-nsut', '<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">Diễn vi&ecirc;n Kiều Thanh từng để lại nhiều dấu ấn với vai Tr&agrave; Cave trong phim truyền h&igrave;nh &#39;Ph&iacute;a trước l&agrave; bầu trời&#39; c&oacute; t&ecirc;n trong danh s&aacute;ch c&aacute;c NSƯT mới được phong đợt n&agrave;y.</span></span></p>\n', 'Kiều Thanh được phong NSƯT', 'Diễn viên Kiều Thanh từng để lại nhiều dấu ấn với vai Trà Cave trong phim truyền hình \'Phía trước là bầu trời\' có tên trong danh sách các NSƯT mới được phong đợt này.', '7137761.jpg', '15/07/2015 12:34', '<p><strong>Diễn vi&ecirc;n Kiều Thanh từng để lại nhiều dấu ấn với vai Tr&agrave; Cave trong phim truyền h&igrave;nh &#39;Ph&iacute;a trước l&agrave; bầu trời&#39; c&oacute; t&ecirc;n trong danh s&aacute;ch c&aacute;c NSƯT mới được phong đợt n&agrave;y.</strong></p>\n\n<p>&nbsp;</p>\n\n<div style=\"border: 0px none; margin: 0px; outline: none 0px; padding: 0px; background: none 0px 0px repeat scroll transparent;\">&nbsp;</div>\n\n<p><strong><img alt=\"\'Trà Cave\', Kiều Thanh, diễn viên, NSƯT\" src=\"http://imgs.vietnamnet.vn/Images/vnn/2015/07/13/16/20150713163833-2.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px none; margin:0px auto; max-width:720px; outline:none 0px; padding:0px\" title=\"\'Trà Cave\', Kiều Thanh, diễn viên, NSƯT\" /></strong></p>\n\n<p>Kiều Thanh l&agrave; gương mặt quen thuộc tr&ecirc;n truyền h&igrave;nh với những vai diễn đa dạng. L&agrave; diễn vi&ecirc;n của Nh&agrave; h&aacute;t Kịch H&agrave; Nội nhưng Kiều Thanh lại hoạt động mạnh nhất ở mảng diễn vi&ecirc;n truyền h&igrave;nh. C&ocirc; để lại nhiều dấu ấn với những vai số phận v&agrave; sắc sảo, từ Tr&agrave; Cave trong &#39;Ph&iacute;a trước l&agrave; bầu trời&#39; đến B&aacute;ch Thanh trong &#39;Cuồng phong&#39; v&agrave; mới nhất l&agrave; vai Kiều Loan - một phụ nữ sắc sảo, chủ một doanh nghiệp gỗ trong &#39;Khi đ&agrave;n chim trở về 3&#39; đang ph&aacute;t s&oacute;ng tr&ecirc;n VTV1.</p>\n\n<p>Kiều Loan l&agrave; vai diễn Kiều Thanh chờ đợi đ&atilde; l&acirc;u nhưng lại l&agrave; vai diễn rất kh&oacute; với diễn biến t&acirc;m l&yacute; phức tạp, kh&aacute;c hẳn những vai phản diện c&ocirc; từng đảm nhiệm. &quot;Kiều Loan l&agrave; một nh&acirc;n vật rất đa chiều, thậm ch&iacute; c&oacute; đến 4 sắc th&aacute;i kh&aacute;c nhau. Phong c&aacute;ch, cử chỉ, t&aacute;c phong của nh&acirc;n vật cũng mang một m&agrave;u sắc kh&aacute;c hẳn. Kiều Loan c&oacute; l&uacute;c thể hiện h&igrave;nh ảnh một người con g&aacute;i chững chạc, c&oacute; thể trở th&agrave;nh người y&ecirc;u l&yacute; tưởng, nhưng cũng c&oacute; l&uacute;c khiến kh&aacute;n giả thực sự căm gh&eacute;t bởi những thủ đoạn của m&igrave;nh&quot;, c&ocirc; n&oacute;i về vai diễn mới nhất.</p>\n\n<p><img alt=\"\'Trà Cave\', Kiều Thanh, diễn viên, NSƯT\" src=\"http://imgs.vietnamnet.vn/Images/vnn/2015/07/13/16/20150713163833-1.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px none; margin:0px auto; max-width:720px; outline:none 0px; padding:0px\" title=\"\'Trà Cave\', Kiều Thanh, diễn viên, NSƯT\" /><br />\n<em>Kiều Thanh tiếp tục v&agrave;o vai phản diện trong &#39;Khi đ&agrave;n chim trở về 3&#39;.</em></p>\n\n<p>Kiều Thanh cho hay ch&iacute;nh đạo diễn Danh Dũng l&agrave; người đ&atilde; ph&aacute;t hiện ra khả năng diễn xuất của Kiều Thanh v&agrave; giao cho c&ocirc; vai diễn đầu ti&ecirc;n trong phim Cảnh s&aacute;t h&igrave;nh sự. Kiều Thanh cũng l&agrave; diễn vi&ecirc;n đồng h&agrave;nh với cả 3 phần phim.</p>\n\n<p>Tuy nhi&ecirc;n, Kiều Thanh n&oacute;i c&ocirc; lu&ocirc;n cảm ơn đạo diễn Đỗ Thanh Hải v&igrave; anh l&agrave; người c&oacute; c&ocirc;ng &#39;khai ph&aacute;&#39; c&ocirc;, v&agrave; d&agrave;nh cho c&ocirc; vai diễn nhiều dấu ấn trong &#39;Ph&iacute;a trước l&agrave; bầu trời&#39;. Từ vai diễn n&agrave;y, c&ocirc; chứng tỏ khả năng diễn đa dạng v&agrave; th&agrave;nh c&ocirc;ng với nhiều thể loại vai, từ chủ doanh nghiệp đến em g&aacute;i một &ocirc;ng tr&ugrave;m ma t&uacute;y, một g&aacute;i điếm hay người t&igrave;nh của chủ tịch tỉnh....</p>\n\n<p>D&ugrave; c&oacute; một đời sống phim ảnh bận rộn nhưng Kiều Thanh lại rất k&iacute;n tiếng trong việc chia sẻ đời sống ri&ecirc;ng tư. C&aacute;ch đ&acirc;y v&agrave;i năm c&ocirc; đột ngột tuy&ecirc;n bố đ&atilde; l&agrave;m mẹ đơn th&acirc;n nhưng chưa bao giờ c&ocirc;ng khai về bố đứa b&eacute;. Kiều Thanh cũng được đ&aacute;nh gi&aacute; l&agrave; 1 trong những diễn vi&ecirc;n m&agrave; truyền th&ocirc;ng kh&oacute; tiếp cận nhất, ngay cả khi muốn khai th&aacute;c về chuyện nghề nghiệp.</p>\n\n<p>&#39;Khi đ&agrave;n chim trở về 3&#39; ph&aacute;t s&oacute;ng l&agrave; l&uacute;c c&ocirc; đ&oacute;n nhận tin được phong tặng NSƯT đợt n&agrave;y (c&ugrave;ng với diễn vi&ecirc;n Linh Huệ, Tiến Hợi). C&aacute;c diễn vi&ecirc;n của Nh&agrave; h&aacute;t Kịch H&agrave; Nội nơi c&ocirc; c&ocirc;ng t&aacute;c cũng c&oacute; diễn vi&ecirc;n Trung Hiếu (đ&oacute;ng c&ugrave;ng Kiều Thanh trong<em>&nbsp;Cuồng phong, Vệt nắng cuối trời</em>), Tiến Đạt được x&eacute;t NSND đợt n&agrave;y.&nbsp;</p>\n\n<p><strong>Thu An</strong></p>\n', 0, 0, 7),
(17, 'Trích Đoạn Trong Phim The Treacherous Cháy Vé Xứ Hàn', 'trich-doan-trong-phim-the-treacherous-chay-ve-xu-han', '<p>Tr&iacute;ch đoạn trong p.him The Treacherous ch&aacute;y v&eacute; xứ H&agrave;n</p>\n', 'Trích Đoạn Trong Phim The Treacherous Cháy Vé Xứ Hàn', 'Trích đoạn trong p.him The Treacherous cháy vé xứ Hàn', '7137762.jpg', '15/07/2015 12:45', '<p style=\"text-align:justify\">Hai tr&iacute;ch đoạn của The Treacherous khiến người xem thực sự bị sốc bởi nội dung &ldquo;khủng khiếp&rdquo; chẳng k&eacute;m g&igrave; một bộ phim &quot;Mặn&quot;.</p>\n\n<p>The Treacherous (Vương triều dục vọng) ph&aacute;t h&agrave;nh tại H&agrave;n Quốc v&agrave;o th&aacute;ng 5 vừa qua l&agrave; bộ phim xoay quanh vị vua Yeonsangu, người được cho l&agrave; hoang d.&acirc;m nhất trong lịch sử vua ch&uacute;a xứ Kim chi.&nbsp;Ngay từ những ng&agrave;y đầu c&ocirc;ng chiếu, phim g&acirc;y sốt dư luận H&agrave;n bởi h&agrave;ng loạt cảnh &ldquo;n&oacute;ng&rdquo; trần trụi.</p>\n\n<p style=\"text-align:justify\">&nbsp;</p>\n\n<div class=\"imgslide\" style=\"border: 0px; font-size: 15px; margin: 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; cursor: -webkit-zoom-in; line-height: 18.5714282989502px; text-align: center; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\"><img src=\"http://v2.cdn.ringring.vn/JgXXlF23iLlZaP3hSsEY/640x10000x2/image/0/0/697/714073.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px; box-sizing:border-box; margin:0px; max-width:720px; outline:none 0px; padding:0px; vertical-align:baseline; width:546px\" /></div>\n\n<p style=\"text-align:center\"><em>Những m&agrave;n &quot;khoe th&acirc;n tập thể&quot; trong p.him</em></p>\n\n<p style=\"text-align:center\">&nbsp;</p>\n\n<div class=\"imgslide\" style=\"border: 0px; font-size: 15px; margin: 0px; outline: none 0px; padding: 0px; box-sizing: border-box; font-family: Arial, sans-serif; vertical-align: baseline; cursor: -webkit-zoom-in; line-height: 18.5714282989502px; text-align: center; background-image: none; background-attachment: scroll; background-size: initial; background-origin: initial; background-clip: initial; background-position: 0px 0px; background-repeat: repeat;\"><em><img src=\"http://v2.cdn.ringring.vn/JgXXlF23iLlZaP3hSsEY/640x10000x2/image/0/0/697/714074.jpg\" style=\"background:none 0px 0px repeat scroll transparent; border:0px; box-sizing:border-box; margin:0px; max-width:720px; outline:none 0px; padding:0px; vertical-align:baseline; width:546px\" /></em></div>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><em>Quang cảnh buổi học về tuyệt chi&ecirc;u l&agrave;m h&agrave;i l&ograve;ng vua</em></p>\n\n<p style=\"text-align:justify\">Trong phim, c&aacute;c c&ocirc; g&aacute;i trẻ đẹp ở khắp đất nước được đưa v&agrave;o cung, được đ&agrave;o tạo những &quot;m&oacute;n nghề&quot; để phục vụ th&uacute; vui x&aacute;c thị của vua. Phim kh&ocirc;ng chỉ khiến người xem &ldquo;n&oacute;ng mắt&rdquo; với những m&agrave;n khoe th&acirc;n của loạt diễn vi&ecirc;n nữ, m&agrave; c&ograve;n &ldquo;mướt mồ h&ocirc;i&rdquo; với h&agrave;ng loạt cảnh giường chiếu. Thậm ch&iacute;, trong nhiều cảnh, to&agrave;n bộ th&acirc;n thể của c&aacute;c diễn vi&ecirc;n nữ đều nude ho&agrave;n to&agrave;n, những điểm nhạy cảm tr&ecirc;n cơ thể được quay cận cảnh.</p>\n\n<p style=\"text-align:justify\">Chỉ với hai tr&iacute;ch đoạn, nhưng người xem phần n&agrave;o đo&aacute;n được độ &ldquo;n&oacute;ng&rdquo; của The Treacherous cũng như l&yacute; do v&igrave; sao phim lại khiến c&aacute;c rạp phim tại H&agrave;n &ldquo;ch&aacute;y v&eacute;&rdquo;.</p>\n\n<p style=\"text-align:center\"><iframe frameborder=\"0\" height=\"400\" scrolling=\"no\" src=\"https://www.youtube.com/embed/FbOWuUNf0eg\" style=\"border-width: 0px; border-style: none; margin: 0px; outline: none 0px; padding: 0px; background: none 0px 0px repeat scroll transparent;\" width=\"600\"></iframe></p>\n\n<p style=\"text-align:center\"><em>V&aacute;n cược d.&acirc;m dục nhất m&agrave;n ảnh&nbsp;</em></p>\n\n<p style=\"text-align:center\">Trong tr&iacute;ch đoạn đầu ti&ecirc;n, c&aacute;c mỹ nữ với trang phục ch&egrave;n &eacute;p v&ograve;ng 1, được học những b&agrave;i học để l&agrave;m sao &ldquo;giải tr&iacute;&rdquo; cho vua tốt nhất. Tr&iacute;ch đoạn thứ 2 m&ocirc; tả về một lần &ldquo;th&aacute;c loạn&rdquo; của vị quan trong triều đ&igrave;nh.&nbsp;</p>\n\n<p style=\"text-align:center\"><em>Trailer The Treacherous - Bộ phim về bạo ch&uacute;a hoang d.&acirc;m nhất lịch sử H&agrave;n Quốc</em></p>\n\n<p style=\"text-align:center\"><iframe frameborder=\"0\" height=\"400\" scrolling=\"no\" src=\"https://www.youtube.com/embed/GSqpZV7dp0A\" style=\"border-width: 0px; border-style: none; margin: 0px; outline: none 0px; padding: 0px; background: none 0px 0px repeat scroll transparent;\" width=\"600\"></iframe></p>\n', 0, 0, 5),
(19, 'Oanh Yến Khoe Sắc Với Áo Dài', 'oanh-yen-khoe-sac-voi-ao-dai', '<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">Top 15 Hoa hậu Thế giới người Việt 2010 chọn trang phục truyền thống c&oacute; họa tiết, sắc m&agrave;u nổi bật.</span></span></p>\n', 'Oanh Yến Khoe Sắc Với Áo Dài', 'Oanh Yến Khoe Sắc Với Áo Dài', '7137763.jpg', '15/07/2015 01:02', '<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"border-collapse:collapse; border-spacing:0px; color:rgb(0, 0, 0); font-family:arial; font-size:14px; line-height:normal; margin:0px auto 10px; max-width:100%; min-width:1px; width:1px\">\n	<tbody>\n		<tr>\n			<td style=\"vertical-align:middle\"><img alt=\"1-1-jpeg-4191-1436844856.jpg\" src=\"http://c1.f21.img.vnecdn.net/2015/07/14/1-1-jpeg-4191-1436844856.jpg\" /></td>\n		</tr>\n		<tr>\n			<td style=\"vertical-align:middle\">\n			<p>Người đẹp<strong>&nbsp;Oanh Yến&nbsp;</strong>xuất hiện trong bộ ảnh mới với t&agrave; &aacute;o d&agrave;i thướt tha, duy&ecirc;n d&aacute;ng. C&ocirc; thử nghiệm nhiều họa tiết, m&agrave;u sắc kh&aacute;c nhau để thay đổi vẻ ngo&agrave;i.</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"border-collapse:collapse; border-spacing:0px; color:rgb(0, 0, 0); font-family:arial; font-size:14px; line-height:normal; margin:0px auto 10px; max-width:100%; min-width:1px; width:1px\">\n	<tbody>\n		<tr>\n			<td style=\"vertical-align:middle\"><img alt=\"1-2-jpeg-6890-1436844856.jpg\" src=\"http://c1.f21.img.vnecdn.net/2015/07/14/1-2-jpeg-6890-1436844856.jpg\" /></td>\n		</tr>\n		<tr>\n			<td style=\"vertical-align:middle\">\n			<p>Họa tiết vẽ tay, hoa l&aacute;, tr&aacute;i c&acirc;y... được thể hiện sinh động tr&ecirc;n trang phục truyền thống. Thiết kế c&ograve;n kết hợp cả xu hướng chấm bi, pop-art mang lại n&eacute;t đẹp hiện đại cho quốc phục.</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"border-collapse:collapse; border-spacing:0px; color:rgb(0, 0, 0); font-family:arial; font-size:14px; line-height:normal; margin:0px auto 10px; max-width:100%; min-width:1px; width:1px\">\n	<tbody>\n		<tr>\n			<td style=\"vertical-align:middle\"><img alt=\"2-1-1348-1436844856.jpg\" src=\"http://c1.f21.img.vnecdn.net/2015/07/14/2-1-1348-1436844856.jpg\" /></td>\n		</tr>\n		<tr>\n			<td style=\"vertical-align:middle\">\n			<p>Top 15 Hoa hậu thế giới người Việt rực rỡ c&ugrave;ng &aacute;o d&agrave;i cổ thuyền, in hoa to bản rực rỡ.</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"border-collapse:collapse; border-spacing:0px; color:rgb(0, 0, 0); font-family:arial; font-size:14px; line-height:normal; margin:0px auto 10px; max-width:100%; min-width:1px; width:1px\">\n	<tbody>\n		<tr>\n			<td style=\"vertical-align:middle\"><img alt=\"10-jpeg-6874-1436844856.jpg\" src=\"http://c1.f21.img.vnecdn.net/2015/07/14/10-jpeg-6874-1436844856.jpg\" /></td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" class=\"tplCaption\" style=\"border-collapse:collapse; border-spacing:0px; color:rgb(0, 0, 0); font-family:arial; font-size:14px; line-height:normal; margin:0px auto 10px; max-width:100%; min-width:1px; width:1px\">\n	<tbody>\n		<tr>\n			<td style=\"vertical-align:middle\"><img alt=\"2-3-6235-1436844857.jpg\" src=\"http://c1.f21.img.vnecdn.net/2015/07/14/2-3-6235-1436844857.jpg\" /></td>\n		</tr>\n		<tr>\n			<td style=\"vertical-align:middle\">\n			<p>Biến tấu &aacute;o d&agrave;i trắng độc đ&aacute;o, c&aacute;ch điệu từ &aacute;o b&agrave; ba, đi k&egrave;m quần họa tiết hoa sen trang nh&atilde;, đậm hồn Việt.</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<table align=\"center\" border=\"0\" cell